import React from 'react';

type ProgressBarProps = {
  status: string;
  late?: boolean;
};

const statusToVal = (status: string) => {
  switch (status) {
    case 'not_started':
      return 0;
    case 'on_the_way':
      return 1;
    case 'arrived':
      return 2;
    case 'no_show':
      return 3;
    case 'picked_up':
      return 4;
    case 'completed':
      return 5;
    default:
      return -1;
  }
};

